// Updated checkImbuementDecay function to fix PZ logic\nvoid checkImbuementDecay(Player* player) {\n    // Speed and Capacity always decay regardless of PZ\n    if (hasImbuement(player, IMBUEMENT_SPEED)) {\n        decayImbuement(player, IMBUEMENT_SPEED);\n    }\n    if (hasImbuement(player, IMBUEMENT_CAPACITY)) {\n        decayImbuement(player, IMBUEMENT_CAPACITY);\n    }\n    // Other imbuements skip decay when player is in protection zone\n    if (!player->isInProtectionZone()) {\n        if (hasImbuement(player, IMBUEMENT_STRENGTH)) {\n            decayImbuement(player, IMBUEMENT_STRENGTH);\n        }\n        // Add logic for other imbuements here\n    }\n    // Removed buggy logic for isInBackpack and nonAggressiveFightOnly\n}